import{_ as r,c as a,o as t,ai as l}from"./chunks/framework.C2xawtbv.js";const i="/WebUkrainianStuff/pages/charities.jpg",d=JSON.parse('{"title":"Благодійні організації","description":"","frontmatter":{},"headers":[],"relativePath":"pages/charities.md","filePath":"pages/charities.md","lastUpdated":1716050681000}'),o={name:"pages/charities.md"};function h(n,e,p,s,f,u){return t(),a("div",null,e[0]||(e[0]=[l('<p><img src="'+i+'" alt="Title"></p><h1 id="благодіині-організаціі" tabindex="-1">Благодійні організації <a class="header-anchor" href="#благодіині-організаціі" aria-label="Permalink to &quot;Благодійні організації&quot;">​</a></h1><ul><li><a href="#благодіині-організаціі-які-допомагають-у-віині">Благодійні організації, які допомагають у війні</a></li><li><a href="#загальна-допомога-для-украінців">Загальна допомога для українців</a></li><li><a href="#допомога-для-переміщених-осіб">Допомога для переміщених осіб</a></li><li><a href="#благодіині-організаціі-які-допомагають-дітям">Благодійні організації, які допомагають дітям</a></li><li><a href="#благодіині-організаціі-для-літніх-людеи">Благодійні організації для літніх людей</a></li><li><a href="#благодіині-організаціі-які-допомагають-жінкам">Благодійні організації, які допомагають жінкам</a></li><li><a href="#благодіині-організаціі-для-донорства-крові">Благодійні організації для донорства крові</a></li><li><a href="#благодіині-організаціі-для-тварин">Благодійні організації для тварин</a></li><li><a href="#благодіині-організаціі-для-захисту-довкілля">Благодійні організації для захисту довкілля</a></li><li><a href="#благодіині-організаціі-які-допомагають-у-боротьбі-з-коронавірусом">Благодійні організації, які допомагають у боротьбі з коронавірусом</a></li></ul><p><strong>Ласкаво просимо! Ось наш затверджений список</strong></p><p>Нижче ви знайдете упорядкований список благодійних фондів та організацій, які ми перевірили, верифікували та затвердили. Цей список існує для того, щоб наші користувачі могли спокійно обирати організацію для підтримки.</p><p><a href="https://www.reddit.com/r/ukraine/wiki/charities-vetting/" target="_blank" rel="noreferrer">Читайте про наш процес перевірки тут</a></p><p>Список взято <a href="https://www.reddit.com/r/ukraine/wiki/charities/?rdt=57463" target="_blank" rel="noreferrer">звідси</a></p><h2 id="благодіині-організаціі-які-допомагають-у-віині" tabindex="-1">Благодійні організації, які допомагають у війні <a class="header-anchor" href="#благодіині-організаціі-які-допомагають-у-віині" aria-label="Permalink to &quot;Благодійні організації, які допомагають у війні&quot;">​</a></h2><ul><li><p><a href="https://u24.gov.ua/" target="_blank" rel="noreferrer">Об&#39;єднані24</a>: цей сайт був започаткований президентом Зеленським як основний майданчик для збору благодійних пожертв на підтримку України. Кошти будуть спрямовані на покриття найнагальніших потреб, що стоять перед Україною.</p></li><li><p><a href="https://savelife.in.ua/" target="_blank" rel="noreferrer">Повернись живим</a>: ця неурядова організація збирає кошти на нелетальне військове обладнання, таке як тепловізійні приціли, і постачає його на передову на Донбасі. Крім того, організація проводить вишколи для українських військових, досліджує потреби війська та займається соціальною реінтеграцією ветеранів.</p></li><li><p><a href="https://tridentdefenseinitiative.com/donate" target="_blank" rel="noreferrer">Оборонна ініціятива &quot;Тризуб&quot;</a>: завдяки цій ініціятиві успішно пройшли підготовку та були споряджені тисячі українських військовослужбовців. Ваші пожертви забезпечать українських військових необхідним спорядженням, обладнанням та навчанням, необхідним для використання аптечок, безпілотників та тепловізійних прицілів.</p></li><li><p><a href="https://www.hospitallers.life/needs-hospitallers" target="_blank" rel="noreferrer">Госпитальєри</a>: це медичний батальйон, який об&#39;єднує добровольців-парамедиків та лікарів, щоб рятувати життя бійців на передовій. Вони збирають кошти на ремонт автомобілів, пальне та медичне обладнання.</p></li><li><p><a href="https://ukrainefrontline.org/" target="_blank" rel="noreferrer">Лінія фронту в Україні</a>: ця громадська організація, що базується в США і зареєстрована згідно з 501(c)(3), задовольняє запити солдатів на передовій щодо безпосереднього захисту та гуманітарної допомоги через своїх представників на місцях.</p></li><li><p><a href="https://energyofrebirth.com/" target="_blank" rel="noreferrer">Енергія Відродження</a>: ця громадська організація надає ефективну допомогу Збройним силам України як медичним, так і бойовим спорядженням, а також гуманітарну допомогу та логістичну підтримку цивільному населенню.</p></li><li><p><a href="https://protectavolunteer.com/" target="_blank" rel="noreferrer">Захисти волонтера</a>: знайдіть волонтера в Україні, який потребує фінансової підтримки або спорядження, або профінансуйте переліт, щоб доставити бійця на передову. Захисти волонтера - це програма, яка з&#39;єднує військових та інших волонтерів з донорами, щоб допомогти з оплатою перельоту, рятувальним спорядженням, медичними наборами, одягом на холодну погоду та іншими витратами.</p></li><li><p><a href="https://theghostconcept.org/" target="_blank" rel="noreferrer">Концепція &quot;Привид&quot;</a>: пряма підтримка іноземних військових добровольців та українських солдатів на передовій в Україні. Спонсорів напряму з&#39;єднують з волонтерами та солдатами в Україні, щоб допомогти забезпечити їх життєво необхідним спорядженням або надати фінансову допомогу. Заповніть форму &quot;Спонсор-привид&quot;, щоб отримати зв&#39;язок з бійцем на передовій.</p></li><li><p><a href="https://renegaderelief.org/" target="_blank" rel="noreferrer">Допомога ренегатам</a> співпрацює з іншими неурядовими організаціями для пошуку та доставки військової, медичної та гуманітарної допомоги туди, де вона необхідна. Зухвалі, хитрі та обережно безстрашні, вони просто хочуть допомогти Україні.</p></li><li><p><a href="https://ukraineaidops.org/" target="_blank" rel="noreferrer">Операції з надання допомоги Україні</a>: волонтери з усього світу, які допомагають знаходити та доставляти обладнання безпосередньо тим, хто його найбільше потребує в Україні.</p></li></ul><h2 id="загальна-допомога-для-украінців" tabindex="-1">Загальна допомога для українців <a class="header-anchor" href="#загальна-допомога-для-украінців" aria-label="Permalink to &quot;Загальна допомога для українців&quot;">​</a></h2><ul><li><p><a href="https://quicknote.io/d2edacb0-fd2d-11ec-b0ea-1752dc463003" target="_blank" rel="noreferrer">Людство</a>: це невелика команда волонтерів, яка займається пошуком та розподілом гуманітарної допомоги серед найбільш вразливих верств населення в тимчасово окупованій Херсонській області.</p></li><li><p><a href="https://www.birdoflightukraine.org/" target="_blank" rel="noreferrer">Птах Світла</a>: Птах Світла створює гуманітарну лінію постачання медичної та оборонної допомоги в найбільш постраждалі регіони України. Працюючи з місцевими та міжнародними партнерами, вони забезпечують швидке реагування, яке важко здійснити великим неурядовим організаціям.</p></li><li><p><a href="https://helppeople.org.ua/" target="_blank" rel="noreferrer">Допомогти людям</a> надає підтримку в евакуації та гуманітарну допомогу вразливим громадам по всій Україні. Вони утримують житло для понад 30 000 біженців і доставили понад 100 000 тонн гуманітарної допомоги, і все це безкоштовно для отримувачів.</p></li><li><p><a href="https://telehelpukraine.com/" target="_blank" rel="noreferrer">TeleHelp Україна</a> надає безкоштовну якісну медичну та психіатричну допомогу українцям, які постраждали від війни, за допомогою телемедичних послуг від глобальної мережі медиків, перекладачів та волонтерів, зосереджуючись на захисті інтересів пацієнтів та координації медичної допомоги.</p></li><li><p><a href="https://laptopsforukraine.com/" target="_blank" rel="noreferrer">Лаптопи для України</a>: ця громадська організація є результатом співпраці між Міністерством цифрової трансформації України та Європейською Комісією. Пожертвуйте свої невикористані пристрої, щоб допомогти вирішити проблему гострої нестачі робочих електронних пристроїв для медсестер, вчителів та студентів в Україні.</p></li></ul><h2 id="допомога-для-переміщених-осіб" tabindex="-1">Допомога для переміщених осіб <a class="header-anchor" href="#допомога-для-переміщених-осіб" aria-label="Permalink to &quot;Допомога для переміщених осіб&quot;">​</a></h2><ul><li><p><a href="https://sp4ukraine.org/" target="_blank" rel="noreferrer">Безпечний перехід для України</a>: ця громадська організація допомагає найбільш вразливим верствам населення в Україні, сприяючи наземній евакуації, надаючи допомогу в пересуванні, розселенні та наданні життєво необхідного зимового спорядження.</p></li><li><p><a href="https://www.donbasssos.org/" target="_blank" rel="noreferrer">Донбас SOS</a>: ця організація допомагає тим, хто живе в зоні бойових дій на Донбасі, тим, хто переїхав в інші регіони України, а також звільненим військовополоненим. Серед іншого вона пропонує юридичну підтримку, допомогу з житлом та психологічну допомогу.</p></li><li><p><a href="https://krymsos.com/" target="_blank" rel="noreferrer">Крим SOS</a>: ця організація допомагає внутрішньо переміщеним особам з Криму з часу окупації півострова Росією у 2014 році. Вона документує репресії російської влади проти кримчан і виступає за припинення окупації.</p></li></ul><h2 id="благодіині-організаціі-які-допомагають-дітям" tabindex="-1">Благодійні організації, які допомагають дітям <a class="header-anchor" href="#благодіині-організаціі-які-допомагають-дітям" aria-label="Permalink to &quot;Благодійні організації, які допомагають дітям&quot;">​</a></h2><ul><li><p><a href="https://vstygnemo.org.ua/" target="_blank" rel="noreferrer">ДітиМиЦеЗробимо</a>: цей рух виріс з кампанії, яка зібрала 2 мільйони доларів на найдорожчі у світі ліки для українського хлопчика зі спінальною м&#39;язовою атрофією. Зараз вона збирає кошти на лікування інших українських дітей зі СМА.</p></li><li><p><a href="https://ruka-ob-ruku.com.ua/" target="_blank" rel="noreferrer">Рука об руку</a>: це біговий клуб для дітей з інвалідністю. Ініціятива дає дітям можливість тренуватися та брати участь у забігах разом з батьками та волонтерами.</p></li><li><p><a href="https://www.savethechildren.net/" target="_blank" rel="noreferrer">Врятуйте дітей</a>: це британська благодійна організація, яка допомагає дітям бути в безпеці та здобувати освіту.</p></li></ul><h2 id="благодіині-організаціі-для-літніх-людеи" tabindex="-1">Благодійні організації для літніх людей <a class="header-anchor" href="#благодіині-організаціі-для-літніх-людеи" aria-label="Permalink to &quot;Благодійні організації для літніх людей&quot;">​</a></h2><ul><li><p><a href="https://happyold.com.ua/" target="_blank" rel="noreferrer">Життєлюб</a>: ця благодійна організація забезпечує літніх людей по всій Україні продуктами та ліками, проводить освітні, розважальні та спортивні заходи, а також допомагає з працевлаштуванням. Вони навіть створили модельне агентство для літніх людей.</p></li><li><p><a href="https://letshelp.com.ua/" target="_blank" rel="noreferrer">Давайте допоможемо</a>: ця благодійна організація піклується про самотніх літніх людей і допомагає державним будинкам для літніх людей. Вони також виступають за краще ставлення до літніх людей з боку держави, в тому числі за надання людям у віці 60+ легкого доступу до освіти.</p></li><li><p><a href="https://starenki.com.ua/" target="_blank" rel="noreferrer">Старенькі</a>: це благодійна ініціятива, присвячена проблемам старості в Україні. Вони допомагають самотнім людям поважного віку, забезпечуючи їх продуктами та засобами гігієни.</p></li></ul><h2 id="благодіині-організаціі-які-допомагають-жінкам" tabindex="-1">Благодійні організації, які допомагають жінкам <a class="header-anchor" href="#благодіині-організаціі-які-допомагають-жінкам" aria-label="Permalink to &quot;Благодійні організації, які допомагають жінкам&quot;">​</a></h2><ul><li><p><a href="http://www.women.lviv.ua/" target="_blank" rel="noreferrer">Жіночі перспективи</a>: ця організація допомагає жінкам, які зіткнулися з домашнім насильством, дискримінацією на ринку праці та іншими проблемами. Неурядова організація співпрацює з місцевими та державними органами влади з метою просування гендерної політики в Україні, спрямованої на забезпечення рівности та справедливости.</p></li><li><p><a href="https://marshzhinok.com.ua/" target="_blank" rel="noreferrer">Марш Жінок</a>: щороку 8 березня ця ініціятива проводить акцію на підтримку гендерної рівности та захисту жінок від гендерно зумовленого насильства. Наразі організація подає петицію до України щодо прийняття Стамбульської конвенції.</p></li></ul><h2 id="благодіині-організаціі-для-донорства-крові" tabindex="-1">Благодійні організації для донорства крові <a class="header-anchor" href="#благодіині-організаціі-для-донорства-крові" aria-label="Permalink to &quot;Благодійні організації для донорства крові&quot;">​</a></h2><ul><li><p><a href="https://bloodagents.com/" target="_blank" rel="noreferrer">Агенти крови</a>: це неурядова організація, яка пропагує регулярне, свідоме та безоплатне донорство крови. За останні шість років вони заохотили людей здати кров понад 5 000 разів.</p></li><li><p><a href="https://www.donor.ua/" target="_blank" rel="noreferrer">Донор UA</a>: це автоматизована система рекрутингу та управління донорами крови, покликана сприяти розвитку донорського руху в Україні. Ви можете допомогти, зареєструвавшись і здавши кров або підтримавши проєкт грошовою пожертвою.</p></li></ul><h2 id="благодіині-організаціі-для-тварин" tabindex="-1">Благодійні організації для тварин <a class="header-anchor" href="#благодіині-організаціі-для-тварин" aria-label="Permalink to &quot;Благодійні організації для тварин&quot;">​</a></h2><ul><li><p><a href="https://dogcat.com.ua/" target="_blank" rel="noreferrer">Сіріус</a>: найбільший притулок для безпритульних тварин в Україні, заснований у 2000 році. Його місткість - понад 3 000 тварин. Заклад збирає кошти на корм для тварин, ветеринарні препарати, будівництво та ремонт вольєрів та інші потреби.</p></li><li><p><a href="https://happypaw.ua/" target="_blank" rel="noreferrer">Щаслива Лапа</a>: це благодійна організація, яка займається вирішенням проблем безпритульних тварин в Україні. Благодійна організація допомагає власникам знайти загублених тварин, стерилізує домашніх тварин людей, які цього потребують, та проводить лекції про гуманне ставлення до безпритульних тварин для школярів.</p></li></ul><h2 id="благодіині-організаціі-для-захисту-довкілля" tabindex="-1">Благодійні організації для захисту довкілля <a class="header-anchor" href="#благодіині-організаціі-для-захисту-довкілля" aria-label="Permalink to &quot;Благодійні організації для захисту довкілля&quot;">​</a></h2><ul><li><p><a href="https://nowaste.com.ua/" target="_blank" rel="noreferrer">Україна без сміття</a>: це українська неприбуткова організація, яка просуває практику сортування побутових відходів. Вони навчають компанії, як стати &quot;зеленими&quot; в їхніх офісах, а також проводять лекції широкому загалу.</p></li><li><p><a href="https://laskastore.com/" target="_blank" rel="noreferrer">Ласка</a>: це мережа з двох благодійних магазинів у Києві, які пропагують усвідомлене споживання. Вони приймають пожертвувані речі, 15% з них перепродують, а решту відправляють до дитячих будинків, будинків для людей поважного віку та центрів для людей з інвалідністю.</p></li></ul><h2 id="благодіині-організаціі-які-допомагають-у-боротьбі-з-коронавірусом" tabindex="-1">Благодійні організації, які допомагають у боротьбі з коронавірусом <a class="header-anchor" href="#благодіині-організаціі-які-допомагають-у-боротьбі-з-коронавірусом" aria-label="Permalink to &quot;Благодійні організації, які допомагають у боротьбі з коронавірусом&quot;">​</a></h2><ul><li><p><a href="https://svoyi.org.ua/" target="_blank" rel="noreferrer">Свої</a>: Свої безкоштовно роздають кисневі концентратори людям, які підхопили COVID і не можуть бути ушпиталені через особисті обставини або коли лікарні переповнені. Також допомагають тим, кого виписують занадто рано на користь пацієнтів у більш важкому стані.</p></li><li><p><a href="https://monstrov.org/" target="_blank" rel="noreferrer">Корпорація монстрів</a>: ця організація розташована в Одесі та надає екстрену медичну допомогу людям, які живуть у регіоні. Вони також допомагають лікарням, що працюють в умовах COVID, закуповують ліки та обладнання.</p></li></ul>',27)]))}const k=r(o,[["render",h]]);export{d as __pageData,k as default};
